record.field:
for record in records: if not record.pdf_aval: continue
folder = env.ref('sid_bankbonds_sales_module.folder_avales', raise_if_not_found=False) if not folder: continue
pedidos_txt = ", ".join(record.contract_ids.mapped("name")) if record.contract_ids else "" origen_txt = record.origin_document or "" document_name = (pedidos_txt + " " + origen_txt).strip() or (record.reference or record.name or "Aval") name: record.reference or record.name or document_name or "Aval",
document_vals = { "name": name, "datas": record.pdf_aval, "mimetype": "application/pdf", "res_model": "sid_bonds_orders", "res_id": record.id, "folder_id": folder.id, # Si tienes un campo custom tipo x_name_2 en documents.document, lo puedes poner aquí: # "x_name_2": document_name, }
existing_document = env["documents.document"].search([ ("res_model", "=", "sid_bonds_orders"), ("res_id", "=", record.id), ("folder_id", "=", folder.id), ], limit=1)
if existing_document: existing_document.write(document_vals) else: env["documents.document"].create(document_vals)
